class Indies_Recurringandrentalpayments_Block_Enterprise_CustomerBalance_Checkout_Onepage_Payment_Additional extends Enterprise_CustomerBalance_Block_Checkout_Onepage_Payment_Additional
{
    /**
     * Can display customer balance container
     *
     * @return bool
     */
    public function isDisplayContainer()
    {
        if (!$this->_getCustomer()->getId()) {
            return false;
        }

        if (!$this->getBalance()) {
            return false;
        }

        foreach ($this->getQuote()->getAllItems() as $item)
            if (Mage::helper('recurringandrentalpayments')->isSubscriptionType($item))
                return false;

        return true;
    }
}
